<template>
  <div class="popup">
    <div class="header clearfix fixed-top">
      <button v-bind:disabled="loading" @click="refresh()" class="btn refresh-btn btn-success float-right">
        <span v-show="loading" class="spinner-border spinner-border-sm" role="status" aria-hidden="{{loading}}"></span>
        Refresh
      </button>
      <p>{{pullRequests.length}} Open Pull Requests</p>
    </div>
    <div class="list-group pr-lists">
      <a v-for="pullRequest in pullRequests" v-bind:key="pullRequest.id" target="_blank" v-bind:href="pullRequest.url" class="list-group-item list-group-item-action flex-column align-items-start">
        <div class="d-flex">
          <div>
            <img width="15px" v-bind:src="iconSrc" alt="">            
          </div>
          <div class="w-100 pl-2">
            <div class="d-flex w-100 justify-content-between">
              <h6 class="mb-1 text-dark">{{pullRequest.repo_name}}</h6>
              <small class="text-xxs">{{formatDate(pullRequest.updated_at)}}</small>
            </div>
            <p class="mb-1 text-sm">{{pullRequest.title}}</p>
            <p class="mb-1 text-muted text-xs">
              <span>#{{pullRequest.fromBranchName}}</span>
              <span>by {{pullRequest.username}}</span>
            </p>
          </div>
        </div>
      </a>
    </div>
  </div>
</template>

<script>

import chromeStorage from "../modules/chromeStorage";
import github from "../modules/github";
import dayjs from "dayjs";

export default {
  async created() {
    const storage = await chromeStorage.get(['pullRequests']);
    if (storage.pullRequests) {
      this.pullRequests = storage.pullRequests;    
    } else {
      this.pullRequests = [];
    }
  },
  methods: {
    formatDate: (str) => {
      return dayjs(str).format('YYYY/MM/DD HH:mm');
    },
    refresh: async function() {
      this.loading = true;
      let storage = await chromeStorage.get(['username', 'token', 'repos']);
      await github.fetchAndSavePullRequests(storage.username, storage.token, storage.repos);
      let _storage = await chromeStorage.get(['pullRequests']);
      if (_storage.pullRequests) {
        this.pullRequests = _storage.pullRequests;    
      } else {
        this.pullRequests = [];
      }
      this.loading = false;
    },
  },
  data() {
    return {
      loading: false,
      pullRequests: [],
      iconSrc: "data:image/png;base64,"
    }
  }
};
</script>

